import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import { AuthProvider, useAuth } from './context/AuthContext';
import { CartProvider } from './context/CartContext'; // Importamos el CartProvider
import Login from './components/sesion/Login'; // Ruta hacia Login
import Register from './components/sesion/Register'; // Ruta hacia Register
import Logout from './components/sesion/Logout'; // Ruta hacia Logout
import AdminPanel from './components/AdminPanel'; // Componente de administración

function App() {
  return (
    <AuthProvider>
      <CartProvider> {/* Añadimos el CartProvider aquí */}
        <Router>
          <div className="App">
            <Header />
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login />} /> {/* Ruta para Login */}
              <Route path="/register" element={<Register />} /> {/* Ruta para Register */}
              <Route path="/logout" element={<Logout />} /> {/* Ruta para Logout */}
              <Route path="/admin" element={<AdminRoute />} /> {/* Ruta para Admin */}
            </Routes>
            <Footer />
          </div>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

// Componente para proteger la ruta de administración
const AdminRoute = () => {
  const { user } = useAuth(); // Obtener usuario desde el contexto

  // Verificar si el usuario tiene el rol de Administrador
  return user && user.role === 'Administrador' ? <AdminPanel /> : <Home />;
};

export default App;
