import React from 'react';
import { useCart } from '../context/CartContext';

// Importamos las im치genes
import producto1Img from '../assets/images/producto1.png';
import producto2Img from '../assets/images/producto2.png';
import producto3Img from '../assets/images/producto3.png';

const Products = () => {
  const { addToCart } = useCart();

  // Simulamos una lista de productos con im치genes
  const products = [
    { id: 1, name: 'Producto 1', price: 100, image: producto1Img },
    { id: 2, name: 'Producto 2', price: 200, image: producto2Img },
    { id: 3, name: 'Producto 3', price: 300, image: producto3Img },
  ];

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div>
      <h2>Productos</h2>
      <div style={{ display: 'flex', gap: '20px' }}>
        {products.map((product) => (
          <div key={product.id} style={{ border: '1px solid #ccc', padding: '10px' }}>
            <img
              src={product.image}
              alt={product.name}
              style={{ width: '150px', height: '150px', objectFit: 'cover' }}
            />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => handleAddToCart(product)}>
              Agregar al carrito
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
