import React, { createContext, useContext, useState } from 'react';

// Simulamos una "base de datos" de usuarios con roles
let fakeUsersDB = [
  { username: 'admin', password: 'admin123', role: 'Administrador' },
  { username: 'user1', password: 'user123', role: 'Usuario' },
  { username: 'user2', password: 'user123', role: 'Usuario' }
];

// Creamos el contexto de autenticación
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [users, setUsers] = useState(fakeUsersDB); // Estado para gestionar los usuarios

  // Función para simular inicio de sesión
  const login = (username, password) => {
    const foundUser = users.find(
      (user) => user.username === username && user.password === password
    );

    if (foundUser) {
      setUser(foundUser);
      return null;
    } else {
      return 'Usuario o contraseña incorrectos';
    }
  };

  // Función para registrar un nuevo usuario
  const register = (username, password) => {
    const userExists = users.some(user => user.username === username);
    if (userExists) {
      return 'El usuario ya existe.';
    }

    const newUser = { username, password, role: 'Usuario' };
    setUsers([...users, newUser]); // Añadimos el usuario al estado
    setUser(newUser);
    return null;
  };

  // Función para cerrar sesión
  const logout = () => setUser(null);

  // **Actualizar rol de usuario**
  const updateRole = (username, newRole) => {
    setUsers(users.map((u) =>
      u.username === username ? { ...u, role: newRole } : u
    ));
  };

  // **Eliminar usuario**
  const deleteUser = (username) => {
    setUsers(users.filter((u) => u.username !== username));
  };

  return (
    <AuthContext.Provider value={{ user, users, login, logout, register, updateRole, deleteUser }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext
