// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Product from './pages/Products'; // Importamos Products
import Cart from './components/Cart'; // Importamos Cart
import { AuthProvider } from './context/AuthContext';
import { CartProvider } from './context/CartContext'; // Importamos CartProvider
import Login from './components/sesion/Login';
import Register from './components/sesion/Register';
import Logout from './components/sesion/Logout';
import AdminPanel from './components/AdminPanel';

function App() {
  return (
    <AuthProvider>
      <CartProvider>
        <Router>
          <div className="App">
            <Header />
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/products" element={<Products />} /> {/* Ruta para productos */}
              <Route path="/cart" element={<Cart />} /> {/* Ruta para carrito */}
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/logout" element={<Logout />} />
              <Route path="/admin" element={<AdminRoute />} />
            </Routes>
            <Footer />
          </div>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

const AdminRoute = () => {
  const { user } = useAuth();
  return user && user.role === 'Administrador' ? <AdminPanel /> : <Home />;
};

export default App;
