import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';

const Register = () => {
  const { login } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [users, setUsers] = useState([]); // Simulamos un almacenamiento de usuarios

  const handleSubmit = (e) => {
    e.preventDefault();
    // Crear nuevo usuario y asignarles un rol básico
    const newUser = { username, password, role: 'Usuario' };
    setUsers([...users, newUser]); // Añadir el usuario al "almacenamiento"
    login({ username }, 'Usuario'); // Hacer login automáticamente con el nuevo usuario
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Registrarse</h3>
      <input
        type="text"
        placeholder="Usuario"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Registrarse</button>
    </form>
  );
};

export default Register;
