import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import styles from '../../css/Login.module.css';

const Login = () => {
  const { login } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
  
    if (!username || !password) {
      setError('Por favor, ingresa un usuario y una contraseña.');
      return;
    }

    const loginError = login(username, password); // Captura el error devuelto por login

    if (loginError) {
      setError(loginError); // Si hay error, mostrarlo
    } else {
      setError(''); // Limpiar error si login es exitoso
    }
  };
  

  return (
    <div className={styles.loginPage}>
      <div className={styles.loginFormWrapper}>
        <h3>Iniciar sesión</h3>
        {error && <p className={styles.errorMessage}>{error}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Usuario"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit">Iniciar sesión</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
