import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Logout from './sesion/Logout';
import styles from '..pages/css/He'; 

const Header = () => {
  const { user } = useAuth();

  return (
    <header>
      <h1>Ébano & Bronce</h1>
      <nav>
        <Link to="/">Inicio</Link>
        <Link to="/sobrenosotros">Sobre Nosotros</Link>
        <Link to="/products">Productos</Link>

        {/* Mostrar el enlace al carrito solo si el usuario está autenticado y no es administrador */}
        {user && user.role !== 'Administrador' && <Link to="/cart">Carrito</Link>}

        {user ? (
          <>
            <p>Bienvenido, {user.username}</p>
            <Logout />
            {user.role === 'Administrador' && <Link to="/admin">Panel de Administración</Link>}
          </>
        ) : (
          <>
            <Link to="/login">Iniciar sesión</Link>
            <Link to="/register">Registrarse</Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
