import React from 'react';
import { useAuth } from '../context/AuthContext';
import styles from '../css/AdminPanel.module.css'; // Importa los estilos del módulo CSS

const AdminPanel = () => {
  const { user, users, updateUserRole, deleteUser } = useAuth();

  if (user?.role !== "Administrador") {
    return <p className={styles.accessDenied}>No tienes acceso a esta sección.</p>;
  }

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Panel de Administrador</h2>
      <p>Gestiona usuarios y roles.</p>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>Usuario</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u, index) => (
            <tr key={index}>
              <td>{u.username}</td>
              <td>{u.role}</td>
              <td>
                <button onClick={() => updateUserRole(u.username, "Administrador")}>
                  Hacer Admin
                </button>
                <button onClick={() => updateUserRole(u.username, "Usuario")}>
                  Hacer Usuario
                </button>
                <button onClick={() => deleteUser(u.username)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
