import React from 'react';
import { useCart } from '../context/CartContext';

// Importamos las im치genes
import anillo1 from '../assets/images/eclat_rubis.png';
import anillo2 from '../assets/images/flamme_rouge.png';
import anillo3 from '../assets/images/serenite_emeraude.png';
import anillo4 from '../assets/images/legende_verte.png';
import anillo5 from '../assets/images/emeraude_royale.png';
import anillo6 from '../assets/images/etoile_argentee.png';
import aretes1 from '../assets/images/reflet_lune.png';
import aretes2 from '../assets/images/monnaie_dor.png';
import brazalete1 from '../assets/images/cirque_dargent.png';
import brazalete2 from '../assets/images/flamme_imperiale.png';
import brazalete3 from '../assets/images/saphir_celeste.png';
import collar1 from '../assets/images/lumiere_azur.png';
import collar2 from '../assets/images/eclat_saphir.png';
import collar3 from '../assets/images/couronne_rubis.png';
import collar4 from '../assets/images/rouge_eternel.pngpng';
import collar5 from '../assets/images/emeraude_enchantee.png';
import collar6 from '../assets/images/harmonie_royale.png';


const Products = () => {
  const { addToCart } = useCart();

  // Simulamos una lista de productos con im치genes
  const products = [
    { id: 1, name: 'Producto 1', price: 100, image: producto1Img },
    { id: 2, name: 'Producto 2', price: 200, image: producto2Img },
    { id: 3, name: 'Producto 3', price: 300, image: producto3Img },
    { id: 4, name: 'Producto 1', price: 100, image: producto1Img },
    { id: 2, name: 'Producto 2', price: 200, image: producto2Img },
    { id: 3, name: 'Producto 3', price: 300, image: producto3Img },
    { id: 1, name: 'Producto 1', price: 100, image: producto1Img },
    { id: 2, name: 'Producto 2', price: 200, image: producto2Img },
    { id: 3, name: 'Producto 3', price: 300, image: producto3Img },
    { id: 1, name: 'Producto 1', price: 100, image: producto1Img },
    { id: 2, name: 'Producto 2', price: 200, image: producto2Img },
    { id: 3, name: 'Producto 3', price: 300, image: producto3Img },

];

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div>
      <h2>Productos</h2>
      <div style={{ display: 'flex', gap: '20px' }}>
        {products.map((product) => (
          <div key={product.id} style={{ border: '1px solid #ccc', padding: '10px' }}>
            <img
              src={product.image}
              alt={product.name}
              style={{ width: '150px', height: '150px', objectFit: 'cover' }}
            />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => handleAddToCart(product)}>
              Agregar al carrito
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
