import React from 'react';
import { useAuth } from '../context/AuthContext'; // Asegúrate de importar el contexto de autenticación
import AdminPanel from '../components/AdminPanel'; // Componente de panel de administración
import styles from '../css/Home.module.css'; // Importa el archivo de estilos CSS

// Imágenes importadas
import banner1 from '../assets/images/banner/banner1.jpeg';
import banner2 from '../assets/images/banner/banner2.jpeg';
import banner3 from '../assets/images/banner/banner3.jpeg';

const Home = () => {
  const { user } = useAuth(); // Obtener el usuario actual desde el contexto

  return (
    <div className={styles.homeContainer}>
      {/* Banner principal */}
      <div className={styles.mainBanner}>
        <img src={banner1} alt="Banner principal de Ébano & Bronce" className={styles.bannerImage} />
        <div className={styles.bannerText}>
          <h1>Bienvenidos a Ébano & Bronce</h1>
          <p>El lugar donde la elegancia se encuentra con la calidad.</p>
        </div>
      </div>

      <div className={styles.contentWrapper}>
        {/* Texto introductorio */}
        <section className={styles.introduction}>
          <h2>Tu tienda de confianza</h2>
          <p>
            En Ébano & Bronce, nos especializamos en ofrecer productos únicos que combinan la belleza del ébano y la
            sofisticación del bronce. Descubre nuestra amplia gama de artículos diseñados para realzar tu estilo y hogar.
          </p>
        </section>

        {/* Banner secundario */}
        <div className={styles.secondaryBanner}>
          <img src={banner2} alt="Banner secundario" className={styles.bannerImage} />
          <div className={styles.bannerText}>
            <h2>Ediciones Limitadas</h2>
            <p>¡No te pierdas nuestras ofertas exclusivas y limitadas!</p>
          </div>
        </div>

        {/* Mostrar el panel de administración solo si el usuario tiene el rol de "Administrador" */}
        {user?.role === 'Administrador' ? (
          <AdminPanel />
        ) : (
          <p className={styles.accessDenied}>No tienes permisos para acceder a esta sección.</p>
        )}

        {/* Banner terciario */}
        <div className={styles.tertiaryBanner}>
          <img src={banner3} alt="Banner terciario" className={styles.bannerImage} />
          <div className={styles.bannerText}>
            <h3>Transforma tu espacio</h3>
            <p>Encuentra el toque perfecto para cada rincón de tu hogar.</p>
          </div>
        </div>

        {/* Más texto */}
        <section className={styles.aboutUs}>
          <h2>Sobre Nosotros</h2>
          <p>
            En Ébano & Bronce, no solo creamos productos; contamos historias a través de cada pieza. Nuestro equipo de
            diseñadores trabaja incansablemente para ofrecer artículos que no solo sean funcionales, sino también
            artísticos. La fusión de materiales tradicionales con un diseño contemporáneo es lo que nos hace destacar.
          </p>
        </section>
      </div>
    </div>
  );
};

export default Home;
