import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import AdminPanel from './components/AdminPanel';
import { AuthProvider, useAuth } from './context/AuthContext'; // Importa useAuth aquí

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Header />
          <Routes>
            {/* Ruta para la página de inicio */}
            <Route path="/" element={<Home />} />

            {/* Ruta para el panel de administración */}
            <Route path="/admin" element={<AdminRoute />} />
          </Routes>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}

// Componente para proteger la ruta de administración
const AdminRoute = () => {
  const { user } = useAuth(); // Ahora 'useAuth' debería estar definido

  // Mostrar panel si el usuario es administrador
  return user && user.role === 'Administrador' ? <AdminPanel /> : <Home />;
};

export default App;
