import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext'; // Accedemos al contexto de autenticación

const AdminPanel = () => {
  const { user, users, updateRole, deleteUser } = useAuth(); // Traemos los usuarios y métodos

  // Verificamos si el usuario tiene el rol "Administrador"
  if (user?.role !== "Administrador") {
    return <p>No tienes acceso a esta sección.</p>;
  }

  return (
    <div>
      <h2>Panel de Administrador</h2>
      <p>Gestiona los usuarios y roles.</p>
      <table border="1">
        <thead>
          <tr>
            <th>Usuario</th>
            <th>Rol</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u) => (
            <tr key={u.username}>
              <td>{u.username}</td>
              <td>
                <select
                  value={u.role}
                  onChange={(e) => updateRole(u.username, e.target.value)}
                >
                  <option value="Usuario">Usuario</option>
                  <option value="Administrador">Administrador</option>
                </select>
              </td>
              <td>
                <button onClick={() => deleteUser(u.username)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
