import React from 'react';
import { useCart } from '../context/CartContext';

// Importamos las imágenes
import anillo1 from '../assets/images/eclat_rubis.png';
import anillo2 from '../assets/images/flamme_rouge.png';
import anillo3 from '../assets/images/serenite_emeraude.png';
import anillo4 from '../assets/images/legende_verte.png';
import anillo5 from '../assets/images/emeraude_royale.png';
import anillo6 from '../assets/images/etoile_argentee.png';
import aretes1 from '../assets/images/reflet_lune.png';
import aretes2 from '../assets/images/monnaie_dor.png';
import brazalete1 from '../assets/images/cirque_dargent.png';
import brazalete2 from '../assets/images/flamme_imperiale.png';
import brazalete3 from '../assets/images/saphir_celeste.png';
import collar1 from '../assets/images/lumiere_azur.png';
import collar2 from '../assets/images/eclat_saphir.png';
import collar3 from '../assets/images/couronne_rubis.png';
import collar4 from '../assets/images/rouge_eternel.png';
import collar5 from '../assets/images/emeraude_enchantee.png';
import collar6 from '../assets/images/harmonie_royale.png';


const Products = () => {
  const { addToCart } = useCart();

  // Simulamos una lista de productos con imágenes
  const products = [
    { id: 1, name: 'Éclat Rubis', price: 100, image: anillo1 },
    { id: 2, name: 'Flamme Rouge', price: 200, image: anillo2 },
    { id: 3, name: 'Sérénité Émeraude', price: 300, image: anillo3 },
    { id: 4, name: 'Légende Verte', price: 100, image: anillo4 },
    { id: 5, name: 'Émeraude Royale', price: 200, image: anillo5 },
    { id: 6, name: 'Producto', price: 300, image: anillo6 },
    { id: 7, name: 'Producto', price: 100, image: aretes1 },
    { id: 8, name: 'Producto', price: 200, image: aretes2 },
    { id: 9, name: 'Producto', price: 300, image: brazalete1 },
    { id: 10, name: 'Producto', price: 100, image: brazalete2 },
    { id: 11, name: 'Saphir Céleste', price: 200, image: collar1 },
    { id: 12, name: 'Lumière Azur', price: 300, image: collar2 },
    { id: 13, name: 'Éclat Saphir', price: 300, image: collar3 },
    { id: 14, name: 'Couronne de Rubis', price: 300, image: collar3 },
    { id: 15, name: 'Rouge Éternel', price: 300, image: collar4 },
    { id: 16, name: 'Émeraude Enchantée', price: 300, image: collar5 },
    { id: 17, name: 'Harmonie Royale', price: 300, image: collar6 },


];

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div>
      <h2>Productos</h2>
      <div style={{ display: 'flex', gap: '20px' }}>
        {products.map((product) => (
          <div key={product.id} style={{ border: '1px solid #ccc', padding: '10px' }}>
            <img
              src={product.image}
              alt={product.name}
              style={{ width: '150px', height: '150px', objectFit: 'cover' }}
            />
            <h3>{product.name}</h3>
            <p>${product.price}</p>
            <button onClick={() => handleAddToCart(product)}>
              Agregar al carrito
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
